mapper.insert:
INSERT INTO tbl_board (
    title
    , content
    , writer
) VALUES (
    #{title}
    , #{content}
    , #{writer}
)

mapper.update:
UPDATE tbl_board
SET
    title     = #{title}
    , content = #{content}
WHERE bno = #{bno}

UPDATE tbl_board
SET replycnt = replycnt + #{amount}
WHERE bno = #{bno}

mapper.select:
UPDATE tbl_board
SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

mapper.insert:
INSERT INTO tbl_attach (
    fullname
    , bno
) VALUES (
    #{fullName}
    , LAST_INSERT_ID()
)
